+++ 
@@ -0,0 +1,169 @@
+<template>
+	<div class="overlay">
+		<div class="container">
+			<div class="container__btn">
+				<div class="container__btn__wrapper">
+					<router-link to="/">Back</router-link>
+					<div class="arrow"></div>
+				</div>
+			</div>
+			<div class="container__content">
+				<div class="container__content__img">
+					<img :src="`${flag}`" alt="" />
+				</div>
+				<div class="container__content__description">
+					<h1>{{ name }}</h1>
+					<div class="country__information">
+						<div class="left">
+							<h3>
+								Native Name: <span>{{ native }}</span>
+							</h3>
+							<h3>
+								Population: <span>{{ population }}</span>
+							</h3>
+							<h3>
+								Region: <span>{{ region }}</span>
+							</h3>
+							<h3>
+								Sub Region: <span>{{ subregion }}</span>
+							</h3>
+							<h3>
+								Capital: <span>{{ capital }}</span>
+							</h3>
+						</div>
+						<div class="right">
+							<h3>
+								Top Level Domain:
+								<span>{{ topLevelDomain[0] }}</span>
+							</h3>
+							<h3>
+								Currencies: <span>{{ currencies[0].name }}</span>
+							</h3>
+							<h3>
+								Language: <span>{{ languages[0].name }}</span>
+							</h3>
+						</div>
+					</div>
+					<div class="borders" v-if="check">
+						<span>Border Countries:</span>
+						<a href="">{{ borders }}</a>
+					</div>
+				</div>
+			</div>
+		</div>
+	</div>
+</template>
+
+<script>
+export default {
+	props: [
+		"name",
+		"native",
+		"population",
+		"flag",
+		"region",
+		"subregion",
+		"capital",
+		"topLevelDomain",
+		"currencies",
+		"languages",
+		"borders",
+	],
+	components: {},
+	data() {
+		return {
+			country: null,
+			vis: true,
+		};
+	},
+	methods: {
+		log() {
+			console.log("emit");
+		},
+		check() {
+			 this.borders.length === [] ? this.vis === false : this.vis === true
+		},
+	mounted() {
+		fetch("https://restcountries.eu/rest/v2/all")
+			.then((res) => res.json())
+			.then((data) => (this.country = data))
+			.catch((err) => console.log(err.message));
+		this.mounted = true;
+	},
+};
+</script>
+
+<style lang="scss" scoped>
+@import "@/assets/_variables.scss";
+.overlay {
+	position: absolute;
+	top: 0;
+	height: 100vh !important;
+	width: 100vw !important;
+	background: rgba(0, 0, 0, 0.637);
+}
+.container {
+	background: rgba(0, 0, 0, 0.637);
+	margin: auto;
+	color: $white;
+	font-size: 1.5rem;
+	&__btn {
+		width: 12rem;
+		padding: 1rem 3rem;
+		margin: 8rem 0 8rem 0;
+		background-color: $darkblue-el;
+		box-shadow: 5px 5px 15px 5px rgba(0, 0, 0, 0.32);
+		a {
+			color: rgb(179, 179, 179);
+		}
+	}
+	&__content {
+		max-height: 35rem;
+		display: flex;
+		gap: 15rem;
+		&__img {
+			width: 40%;
+			img {
+				width: 100%;
+				height: 100%;
+			}
+		}
+	}
+	&__content__description {
+		width: 40%;
+		display: flex;
+		flex-direction: column;
+		justify-content: space-around;
+	}
+	.country__information {
+		display: flex;
+		justify-content: space-between;
+	}
+	.borders {
+		span {
+			padding: 0 1rem 0 0;
+			font-weight: $light;
+		}
+		a {
+			background-color: $darkblue-el;
+			padding: 1rem 2rem;
+			margin-right: 1rem;
+			color: white;
+			box-shadow: 5px 5px 15px 5px rgba(0, 0, 0, 0.32);
+			font-weight: $light;
+		}
+	}
+}
+h3 {
+	font-size: 1.6rem;
+	font-weight: $medium;
+	line-height: 3rem;
+}
+a {
+	text-decoration: none;
+	color: $white;
+}
+span {
+	font-weight: $light;
+}
+</style>

