+@import "@/assets/_variables.scss";
+img {
+	width: 100%;
+	height: 100%;
+	object-fit: cover;
+}
+.text {
+	padding: 2rem 3rem 0rem 3rem;
+	text-align: left;
+
+	@media only screen and (max-width: 1300px) {
+		padding: 2rem 3rem 5rem 3rem;
+	}
+	@media only screen and (max-width: 600px) {
+		margin-bottom: 5rem;
+	}
+}
