+	font-weight: $bold;
+	padding-bottom: 1rem;
+	font-size: 2rem;
+	@media only screen and (max-width: 1300px) {
+		font-size: 1.8rem;
+	}
+	@media only screen and (max-width: 600px) {
+		font-size: 2.4rem;
+	}
+	@media only screen and (max-width: 600px) {
+		margin: 1rem 0 1rem 0;
+	}
+}
+h4 {
+	font-size: 1.4rem;
+	@media only screen and (max-width: 600px) {
+		font-size: 1.9rem;
+	}
+
+	font-weight: $light;
+	line-height: 2.4rem;
+	@media only screen and (max-width: 600px) {
+		line-height: 3.2rem;
+	}
+	color: $verylight-bg;
+}
+span {
+	font-weight: $medium;
+
+	color: $white;
+}
+
+a {
+	text-decoration: none;
+	color: $white;
+}
+</style>
